// Enhanced Document Discovery & Filtering - SINGLE DOCUMENT TEST - Don256 - 2025-08-13 22:58:43
const response = $json;
const inputParams = $('Set Research Parameters').first().json;
const maxDocs = 1; // TESTING: Force to 1 document only
const spreadsheetId = inputParams.output_spreadsheet_id || '';
const executionUser = inputParams.execution_user || 'Don256';
const executionDate = inputParams.execution_date || '2025-08-13';
const executionTime = inputParams.execution_time || '2025-08-13 22:58:43';

console.log('=== SINGLE DOCUMENT TEST - Don256 - 2025-08-13 22:58:43 ===');
console.log('SerpAPI Response Keys:', Object.keys(response));
console.log('Search Info:', response.search_information);

// Validate search results
if (!response.organic_results || !Array.isArray(response.organic_results) || response.organic_results.length === 0) {
  console.log('‚ùå No organic results found');
  return [{ 
    json: { 
      error: 'No search results found for research query', 
      search_info: response.search_information || {},
      response_keys: Object.keys(response),
      total_results: response.search_information?.total_results || 0,
      suggestion: 'Try adjusting your search query, timeframe, or adding terms like "contact", "about", "team", "leadership"',
      processed_by: executionUser,
      timestamp: executionTime
    } 
  }];
}

const results = response.organic_results.slice(0, 5); // Get first 5 to choose from
console.log(`Processing ${results.length} results to find 1 best document`);

// Find the best single document for testing
const processedDocuments = [];

for (let i = 0; i < results.length; i++) {
  const result = results[i];

  if (!result.link) {
    console.log(`Skipping result ${i} - no link`);
    continue;
  }

  const url = result.link;
  const title = (result.title || '').toLowerCase();
  const snippet = (result.snippet || '').toLowerCase();

  // Exclude non-contact domains
  const excludeDomains = [
    'youtube.com', 'facebook.com', 'twitter.com', 'instagram.com',
    'reddit.com', 'pinterest.com', 'tiktok.com', 'linkedin.com/feed',
    'amazon.com', 'ebay.com', 'walmart.com', 'wikipedia.org'
  ];

  // High-value contact indicators
  const contactIndicators = [
    'contact', 'about', 'team', 'leadership', 'company', 'organization',
    'staff', 'management', 'directory', 'people', 'executive', 'founder',
    'board', 'management-team', 'our-team', 'leadership-team', 'meet-our',
    'advisory', 'bio', 'profile', 'who-we-are', 'careers', 'press'
  ];

  // Business quality indicators
  const businessIndicators = [
    'ai', 'artificial intelligence', 'education', 'technology', 'tech',
    'innovation', 'software', 'platform', 'solution', 'company',
    'corporation', 'inc', 'llc', 'ltd', 'enterprise', 'startup'
  ];

  const isExcluded = excludeDomains.some(domain => url.includes(domain));
  const hasContactContent = contactIndicators.some(indicator => 
    url.includes(indicator) || title.includes(indicator) || snippet.includes(indicator)
  );
  const hasBusinessContent = businessIndicators.some(indicator => 
    title.includes(indicator) || snippet.includes(indicator)
  );

  // Calculate relevance score
  let relevanceScore = 0.5; // Base score

  if (hasContactContent) relevanceScore += 0.3;
  if (hasBusinessContent) relevanceScore += 0.2;
  if (result.position <= 3) relevanceScore += 0.2;
  if (url.includes('.edu') || url.includes('.org')) relevanceScore += 0.1;
  if (url.toLowerCase().endsWith('.pdf')) relevanceScore += 0.15;

  // TESTING: Include if not excluded and has ANY relevance (lowered threshold)
  if (!isExcluded && relevanceScore > 0.4) {
    processedDocuments.push({
      json: {
        document_url: url,
        input_type: url.toLowerCase().endsWith('.pdf') ? 'pdf' : 'url',
        spreadsheet_id: spreadsheetId,
        document_title: result.title || '',
        document_snippet: result.snippet || '',
        document_index: 1, // Always 1 for single document test
        source: 'serpapi_single_document_test',
        search_position: result.position || i + 1,
        processed_by: executionUser,
        execution_date: executionDate,
        execution_time: executionTime,
        relevance_score: Math.round(relevanceScore * 100) / 100,
        has_contact_indicators: hasContactContent,
        has_business_indicators: hasBusinessContent,
        research_query: inputParams.search_query,
        timeframe: inputParams.timeframe,
        discovery_timestamp: '2025-08-13T22:58:43Z',
        test_mode: 'single_document'
      }
    });

    // TESTING: Break after finding first suitable document
    console.log(`‚úÖ TESTING: Selected document - ${result.title}`);
    console.log(`URL: ${url}`);
    console.log(`Relevance Score: ${relevanceScore}`);
    break;
  }
}

console.log(`‚úÖ TESTING: Processing 1 document for contact extraction`);

// Ensure we have a document to process
if (processedDocuments.length === 0) {
  // TESTING: If no good documents found, take the first non-excluded one
  for (let i = 0; i < results.length; i++) {
    const result = results[i];
    if (result.link && !excludeDomains.some(domain => result.link.includes(domain))) {
      processedDocuments.push({
        json: {
          document_url: result.link,
          input_type: 'url',
          spreadsheet_id: spreadsheetId,
          document_title: result.title || 'Test Document',
          document_snippet: result.snippet || '',
          document_index: 1,
          source: 'serpapi_fallback_test',
          search_position: result.position || i + 1,
          processed_by: executionUser,
          execution_date: executionDate,
          execution_time: executionTime,
          relevance_score: 0.5,
          has_contact_indicators: false,
          has_business_indicators: true,
          research_query: inputParams.search_query,
          timeframe: inputParams.timeframe,
          discovery_timestamp: '2025-08-13T22:58:43Z',
          test_mode: 'single_document_fallback'
        }
      });
      console.log(`‚úÖ TESTING: Fallback selected - ${result.title}`);
      break;
    }
  }
}

if (processedDocuments.length === 0) {
  return [{ 
    json: { 
      error: 'No suitable documents found for single document test',
      total_raw_results: results.length,
      suggestion: 'All documents were excluded - check filtering criteria',
      search_query: inputParams.search_query,
      timeframe: inputParams.timeframe,
      processed_by: executionUser,
      timestamp: executionTime,
      test_mode: 'single_document_failed'
    } 
  }];
}

console.log(`üß™ SINGLE DOCUMENT TEST READY - Processing: ${processedDocuments[0].json.document_title}`);
return processedDocuments;
